#!/usr/bin/bash

function get-env { echo "${!1}"; }
function set-env { export "$1=$2"; }
function unset-env { unset "$1"; }

function __carapace_lazy() {
  # shellcheck disable=SC1090
  source <(carapace "${1}" bash)
  eval "_${1}_completion"
}

