def print_emoji(codepoint: int):
    char = chr(codepoint)
    name = unicodedata.name(char).lower()
    print(f"{char} {name}")


def list_emojis():
    for emoji in EMOJIS:
        emoji_range = emoji.split("-")
        if len(emoji_range) > 1:
            s, e = int(emoji_range[0], 16), int(emoji_range[1], 16) + 1
            for codepoint in range(s, e):
                print_emoji(codepoint)
        else:
            print_emoji(int(emoji, 16))


def sanitize_emoji() -> str:
    in_readable, _, _ = select.select([sys.stdin], [], [], 20)
    if in_readable:
        return sys.stdin.read().strip()[0]
    sys.exit(1)

def main():
    argv = sys.argv[1:]
    subc = argv[0] if len(argv) > 0 else "list"

    if subc == "decode":
        print(sanitize_emoji(), end="")
        return

    list_emojis()



if __name__ == "__main__":
    main()
